import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ProjectForm from './ProjectForm';
import TaskForm from './TaskForm';
import TaskList from './TaskList';

const Dashboard = () => {
  const [projects, setProjects] = useState([]);

  const fetchProjects = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/projects');
      setProjects(res.data);
    } catch (err) {
      console.error('Failed to fetch projects:', err);
    }
  };

  useEffect(() => {
    fetchProjects();
  }, []);

  return (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 p-4">
      {/* Left Column - Project Creation */}
      <div className="col-span-1 bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-xl font-semibold text-blue-600 mb-4">Create New Project</h2>
        <ProjectForm onProjectAdded={fetchProjects} />
      </div>

      {/* Right Columns - Project Task Process */}
      <div className="col-span-2 space-y-6">
        {projects.length === 0 ? (
          <p className="text-gray-500">No projects available.</p>
        ) : (
          projects.map((project) => (
            <div key={project._id} className="bg-gray-50 p-5 rounded-lg shadow border">
              <h3 className="text-lg font-bold text-indigo-700">{project.name}</h3>
              <p className="text-sm text-gray-600 mb-1">{project.description}</p>
              <p className="text-xs text-gray-500">Start: {new Date(project.start_date).toLocaleDateString()}</p>
              <p className="text-xs text-gray-500 mb-2">End: {new Date(project.end_date).toLocaleDateString()}</p>

              {/* Task Form */}
              <TaskForm projectId={project._id} onTaskAdded={fetchProjects} />

              {/* Task List */}
              <TaskList projectId={project._id} />
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Dashboard;
